import React, {FC} from 'react';
import styled from 'styled-components';
import {Winner}  from './Board';

type ResetScreenProps = {
    winner: Winner;
    onReset(): void;
}

const WinnerHeading = styled.h2`
     color: #333;
  text-align: center;
  margin-top: 0;
  font-size: 4rem;
  text-shadow: -3px 3px #00000066;
  font-family:"Varela Round";
`

export const ResetScreen:FC<ResetScreenProps> = ({winner, onReset}) => {
    return <>
    <WinnerHeading>{winner} is a winner!</WinnerHeading>
    <button onClick={onReset}>Restart</button>
    </>
}
